const NewsItems = ({ mode, title, description, imgUrl, newsUrl }) => {
  const cardModeClass = mode === "dark" ? "dark-mode" : "light-mode";

  return (
    <div className={`card ${cardModeClass} my-3`}>
      <img 
        src={imgUrl || "https://static01.nyt.com/images/2022/03/22/arts/22latenight/22latenight-facebookJumbo.png"} 
        className="card-img-top" 
        alt="news" 
      />
      <div className="card-body">
        <h5 className="card-title">{title || "No Title"}</h5>
        <p className="card-text">{description || "No description available."}</p>
        <a 
          href={newsUrl} 
          target="_blank" 
          rel="noreferrer" 
          className={`btn ${cardModeClass === "dark-mode" ? "btn-light" : "btn-dark"}`}>
          Read More
        </a>
      </div>
    </div>
  );
};

export default NewsItems;
